CREATE TABLE `procurement_plan` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `reporting_year` year(4) NOT NULL COMMENT 'Календарный год, для которого составляется план',
  `number_within_year` int(10) unsigned NOT NULL COMMENT 'Номер закупки внутри годового плана',
  `purchase_name` varchar(510) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Полное официальное наименование закупки',
  `departments_id` int(10) unsigned NOT NULL COMMENT 'Идентификатор отдела, для которого выполняется закупка',
  `product_types_id` int(10) unsigned NOT NULL COMMENT 'Идентификатор типа продукции',
  `purchase_types_id` int(10) unsigned NOT NULL COMMENT 'Идентификатор типа закупки',
  `planned_cost_thou_of_rub` int(10) unsigned NOT NULL COMMENT 'Стоимость в тыс. руб., предусмотренная бюджетом',
  `date_start` date NOT NULL COMMENT 'Предполагаемая дата начала закупки',
  `date_finish` date NOT NULL COMMENT 'Предполагаемая дата окончания закупки',
  PRIMARY KEY (`id`),
  UNIQUE KEY `number_within_year` (`reporting_year`,`number_within_year`),
  KEY `fk_procurement_plan_departments_idx` (`departments_id`),
  KEY `fk_procurement_plan_products_types1_idx` (`product_types_id`),
  KEY `fk_procurement_plan_purchases_types1_idx` (`purchase_types_id`),
  CONSTRAINT `fk_procurement_plan_departments` FOREIGN KEY (`departments_id`) REFERENCES `departments` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `fk_procurement_plan_products_types1` FOREIGN KEY (`product_types_id`) REFERENCES `products_types` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `fk_procurement_plan_purchases_types1` FOREIGN KEY (`purchase_types_id`) REFERENCES `purchases_types` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=89 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
